async function main() {
  console.log('Starting Politics category insertion for 13 countries...')

  for (const country of COUNTRIES_POLITICS) {
    try {
      console.log(`\\nProcessing ${country.name}...`)

      const result = await prisma.locationSummary.create({
        data: {
          name: country.name,
          type: 'country',
          category: 'politics',
          country: country.name,
          lat: country.lat,
          lng: country.lng,
          summary: country.summary,
          issues: JSON.stringify(country.issues),
          topStories: JSON.stringify([]),
          storyCount: 0
        }
      })

      console.log(`✓ Successfully added ${country.name} (${result.id})`)
      console.log(`  Character count: ${country.summary.length}`)
      console.log(`  Issues: ${country.issues.length}`)
    } catch (error) {
      console.error(`✗ Error adding ${country.name}:`, error)
    }
  }

  console.log('\\n=== SUMMARY ===')
  console.log(`Total countries processed: ${COUNTRIES_POLITICS.length}`)
  console.log('All countries: ' + COUNTRIES_POLITICS.map(c => c.name).join(', '))
}

main()
  .catch((e) => {
    console.error('Fatal error:', e)
    process.exit(1)
  })
  .finally(async () => {
    await prisma.$disconnect()
  })
