async function saveAllWriteups() {
  console.log('\\n⚡ ENERGY WRITEUPS - DANIEL YERGIN VOICE')
  console.log(`Saving energy writeups for all 114 countries...`)
  console.log(`Format: Historical sweep, geopolitical depth, oil/gas as power driver\\n`)

  let successCount = 0
  let errorCount = 0

  for (let i = 0; i < ENERGY_WRITEUPS.length; i++) {
    const writeup = ENERGY_WRITEUPS[i]
    try {
      // Extract issues from summary
      const issueMatches = writeup.summary.match(/##\\s+([^\\n]+?)(?=\\n)/g) || []
      const issues = issueMatches
        .map(match => match.replace(/##\\s+/, '').trim())
        .filter(issue => !issue.includes('Bottom Line'))
        .slice(0, 3)

      await prisma.locationSummary.upsert({
        where: {
          name_type_category: {
            name: writeup.name,
            type: 'country',
            category: 'energy'
          }
        },
        create: {
          name: writeup.name,
          type: 'country',
          country: writeup.name,
          lat: writeup.lat,
          lng: writeup.lng,
          category: 'energy',
          summary: writeup.summary,
          issues: JSON.stringify(issues),
          topStories: JSON.stringify([]),
          storyCount: 0,
          updatedAt: new Date()
        },
        update: {
          summary: writeup.summary,
          issues: JSON.stringify(issues),
          updatedAt: new Date()
        }
      })

      successCount++
      console.log(`✅ ${successCount}/${ENERGY_WRITEUPS.length} - ${writeup.name}`)

      // Report every 25 countries
      if (successCount % 25 === 0) {
        console.log(`\\n📊 PROGRESS: ${successCount}/${ENERGY_WRITEUPS.length} completed (${errorCount} errors)\\n`)
      }
    } catch (error) {
      errorCount++
      console.error(`❌ Error saving ${writeup.name}:`, error)
    }
  }

  console.log(`\\n✨ BATCH COMPLETE: ${successCount} succeeded, ${errorCount} failed\\n`)
  console.log(`Current batch: Americas (first 5 countries)`)
  console.log(`Remaining: 109 countries to generate in continuation script\\n`)
}

saveAllWriteups()
  .catch(console.error)
  .finally(() => prisma.$disconnect())
