async function saveLifestyleSummaries(batch: LifestyleSummary[], batchNumber: number) {
  console.log(`\n${'='.repeat(80)}`)
  console.log(`📦 Processing Batch ${batchNumber}`)
  console.log('='.repeat(80))

  let successCount = 0
  let errorCount = 0

  for (const data of batch) {
    try {
      const country = ALL_COUNTRIES.find(c => c.name === data.country)

      if (!country) {
        console.error(`❌ Country not found in coordinates list: ${data.country}`)
        errorCount++
        continue
      }

      console.log(`\n📍 Saving LIFESTYLE for ${country.name}`)

      await prisma.locationSummary.upsert({
        where: {
          name_type_category: {
            name: country.name,
            type: 'country',
            category: 'lifestyle',
          },
        },
        update: {
          summary: data.summary,
          issues: JSON.stringify(data.issues),
          topStories: JSON.stringify([]),
          storyCount: 0,
          updatedAt: new Date(),
        },
        create: {
          name: country.name,
          type: 'country',
          country: country.name,
          lat: country.lat,
          lng: country.lng,
          category: 'lifestyle',
          summary: data.summary,
          issues: JSON.stringify(data.issues),
          topStories: JSON.stringify([]),
          storyCount: 0,
        },
      })

      console.log(`✅ Successfully saved ${country.name} (${data.summary.length} chars)`)
      successCount++

    } catch (error) {
      console.error(`❌ Error saving ${data.country}:`, error)
      errorCount++
    }
  }

  console.log(`\n📊 Batch ${batchNumber} Complete:`)
  console.log(`   ✅ Success: ${successCount}`)
  console.log(`   ❌ Errors: ${errorCount}`)

  return { successCount, errorCount }
}

async function main() {
  console.log('🚀 Starting LIFESTYLE generation for all 128 countries')
  console.log('📝 Content generated directly in session (no API calls)')
  console.log(`📦 Total summaries to process: ${LIFESTYLE_SUMMARIES.length}\n`)

  if (LIFESTYLE_SUMMARIES.length === 0) {
    console.error('❌ No summaries found! Please add LIFESTYLE_SUMMARIES data.')
    return
  }

  let totalSuccess = 0
  let totalErrors = 0

  // Process in batches of 10
  const batchSize = 10
  const batches = []

  for (let i = 0; i < LIFESTYLE_SUMMARIES.length; i += batchSize) {
    batches.push(LIFESTYLE_SUMMARIES.slice(i, i + batchSize))
  }

  console.log(`📦 Processing ${batches.length} batches of up to ${batchSize} countries each\n`)

  for (let i = 0; i < batches.length; i++) {
    const { successCount, errorCount } = await saveLifestyleSummaries(batches[i], i + 1)
    totalSuccess += successCount
    totalErrors += errorCount
  }

  console.log(`\n${'='.repeat(80)}`)
  console.log('🎉 ALL BATCHES COMPLETE')
  console.log('='.repeat(80))
  console.log(`✅ Total Success: ${totalSuccess}`)
  console.log(`❌ Total Errors: ${totalErrors}`)
  console.log(`📊 Success Rate: ${((totalSuccess / (totalSuccess + totalErrors)) * 100).toFixed(1)}%`)
}

main()
  .catch(console.error)
  .finally(() => prisma.$disconnect())
