async function main() {
  console.log('🌍 Infrastructure Writeup Generation (Robert Caro Voice)')
  console.log(`📊 Total: ${Object.keys(INFRASTRUCTURE_WRITEUPS).length} countries pre-generated`)
  console.log()

  let generated = 0
  let skipped = 0

  const countries = Object.keys(INFRASTRUCTURE_WRITEUPS)

  for (let i = 0; i < countries.length; i++) {
    const countryName = countries[i]
    const data = INFRASTRUCTURE_WRITEUPS[countryName]

    // Report progress every 25
    if (i > 0 && i % 25 === 0) {
      console.log(`\n📊 PROGRESS REPORT: ${i}/${countries.length} countries processed`)
      console.log(`   Generated: ${generated}, Skipped: ${skipped}\n`)
    }

    console.log(`[${i + 1}/${countries.length}] ${countryName}`)

    try {
      // Check if already exists
      const existing = await prisma.locationSummary.findUnique({
        where: {
          name_type_category: {
            name: countryName,
            type: 'country',
            category: 'infrastructure'
          }
        }
      })

      if (existing) {
        console.log(`  ⏭️  Already exists - skipping`)
        skipped++
        continue
      }

      // Save to database
      await prisma.locationSummary.create({
        data: {
          name: countryName,
          type: 'country',
          country: countryName,
          lat: data.lat,
          lng: data.lng,
          category: 'infrastructure',
          summary: data.summary,
          issues: JSON.stringify(data.issues),
          topStories: JSON.stringify([]),
          storyCount: 0,
          updatedAt: new Date()
        }
      })

      generated++
      console.log(`  ✅ Generated (${data.summary.length} chars, ${data.issues.length} issues)`)

    } catch (error) {
      console.error(`  ❌ Error:`, error)
    }
  }

  console.log(`\n\n✅ GENERATION COMPLETE!`)
  console.log(`   Generated: ${generated}`)
  console.log(`   Skipped: ${skipped}`)
  console.log(`   Total: ${generated + skipped}/${countries.length}`)
}

main()
  .catch(console.error)
  .finally(() => prisma.$disconnect())
