async function main() {
  console.log(`Starting to insert ${TECHNOLOGY_WRITEUPS.length} technology writeups...`)

  let successCount = 0
  let errorCount = 0

  for (const writeup of TECHNOLOGY_WRITEUPS) {
    try {
      await prisma.locationSummary.upsert({
        where: {
          name_type_category: {
            name: writeup.name,
            type: 'country',
            category: 'technology'
          }
        },
        update: {
          summary: writeup.summary,
          issues: JSON.stringify(writeup.issues),
          topStories: JSON.stringify([]),
          lat: writeup.lat,
          lng: writeup.lng,
          country: writeup.name,
          updatedAt: new Date()
        },
        create: {
          name: writeup.name,
          type: 'country',
          category: 'technology',
          summary: writeup.summary,
          issues: JSON.stringify(writeup.issues),
          topStories: JSON.stringify([]),
          lat: writeup.lat,
          lng: writeup.lng,
          country: writeup.name,
          storyCount: 0
        }
      })
      successCount++
      console.log(`✓ ${writeup.name}`)
    } catch (error) {
      errorCount++
      console.error(`✗ ${writeup.name}:`, error)
    }
  }

  console.log(`\nCompleted: ${successCount} successful, ${errorCount} errors`)
  console.log(`\nProgress: ${successCount} / 128 countries`)
}

main()
  .catch(console.error)
  .finally(() => prisma.$disconnect())
